// contracts/FacialRecognition.sol

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract FacialRecognition {
    mapping(address => bytes32) private userHashes;

    // Event for logging registration
    event UserRegistered(address indexed user, bytes32 hash);

    // Event for logging verification
    event UserVerified(address indexed user, bool success);

    // Register a new user's facial data hash
    function registerUser(bytes32 hash) public {
        userHashes[msg.sender] = hash;
        emit UserRegistered(msg.sender, hash);
    }

    // Verify a user's facial data hash
    function verifyUser(bytes32 newHash) public returns (bool) {
        bool isVerified = (userHashes[msg.sender] == newHash);
        emit UserVerified(msg.sender, isVerified);
        return isVerified;
    }
}
